import { useState } from "react";
import BotonForm from "./BotonForm";

function Formulario({estadoNombre, estadoPass}) {

    const [nombre, setNombre] = useState(estadoNombre);
    const [pass, setPass] = useState(estadoPass);
    let mostrarBoton = false;

    function validarPass(event){

        setPass(event.target.value) 

    }

    function validarNombre(event){
       console.log(mostrarBoton)
        setNombre(event.target.value)
        if(pass == 252525){
         mostrarBoton = true
        }

    }





  return (
    <>
      <div className="col">
        <label>Nombre:</label>
        <input 
            className="form-control" 
            type='text'
            onChange={validarPass}/>
      </div>
      <div className="col">
        <label>Password:</label>
        <input 
            className="form-control" 
            type='text'
            onChange={validarNombre}/>
      </div>

      <div>
        <h1>{pass}</h1>
        <h1>{nombre}</h1>
      </div>
     <div>
        { mostrarBoton == true ? (<BotonForm/>)  : null}       
     </div> 
    </>

  );
}

export default Formulario
